create table render (uuid varchar(64) not null, completed datetime, created datetime, decomposed bit not null, film_json varchar(255) not null, height integer not null, percent_complete integer, png_base64 longtext, renderer_json varchar(255) not null, sampler_json varchar(255) not null, spp integer not null, offsetx integer not null, offsety integer not null, submitted datetime, version bigint not null, width integer not null, parent_uuid varchar(64), scene_id bigint not null, primary key (uuid)) engine=MyISAM;
create table scene (id bigint not null auto_increment, json longtext not null, nickname varchar(255), version bigint not null, primary key (id)) engine=MyISAM;
alter table render add constraint FK_render_parent foreign key (parent_uuid) references render (uuid);
alter table render add constraint FK_render_scene foreign key (scene_id) references scene (id);